import React, { useRef, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

import mapboxgl from 'mapbox-gl/dist/mapbox-gl-csp';
import MapboxWorker from 'worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker';

mapboxgl.workerClass = MapboxWorker;
mapboxgl.accessToken = 'pk.eyJ1IjoiY29oZW5qb3NoMTAiLCJhIjoiY2tvODM3cHViMWh5MDJ3bWxlODR5OHJwbyJ9.OsP4XQxhznEl3LkexbZyNg';


const Map = () => {
  // Map Information
  const mapContainer = useRef();
  const [zoom, setZoom] = useState(9);

  // Technician Information
  const [techLng, setTechLng] = useState(lng);
  const [techLat, setTechLat] = useState(lat);
  const [techTitle, setTechTitle] = useState("Technician 1");
  const [techRot, setTechRot] = useState(90);
  const [techColor, setTechColor] = useState("#" + Math.floor(Math.random()*16777215).toString(16));

  const fetchTechnicianLocation = (time) => {
    return fetch('http://127.0.0.1:5000/api/v1/solar_farms/abc123/technicians')
      .then((response) => response.json())
      .then((json) => {
        console.log(json.features[0].properties.name);
        setTechTitle(json.features[0].properties.name);
        setTechLng(techLng + 1);
        setTechLat(techLat + 1);
        //setTechColor("#" + Math.floor(Math.random()*16777215).toString(16));
      })
      .catch((error) => {
        console.error(error);
      });
    };

  useEffect(() => {
    fetchTechnicianLocation(0);

    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.on('move', () => {
      setLng(map.getCenter().lng.toFixed(4));
      setLat(map.getCenter().lat.toFixed(4));
      setZoom(map.getZoom().toFixed(2));
    });

    var marker1 = new mapboxgl.Marker({color: techColor})
      .setLngLat([techLng, techLat])
      .setPopup(new mapboxgl.Popup().setText(techTitle))
      .setRotation(techRot)
      .addTo(map);

    return () => map.remove();
  }, []);

  return (
    <div>
      <div className="sidebar">
        Longitude: {techLng} | Latitude: {techLat} | Zoom: {zoom} | TechTitle: {techTitle}
      </div>
      <div className="map-container" ref={mapContainer} />
    </div>
  );
};

ReactDOM.render(<Map />, document.getElementById('app'));
